static void loc_prepare(st_plugin_voice_t *_voice);

void loc_prepare(st_plugin_voice_t *_voice) {
   ST_PLUGIN_VOICE_CAST(env_4_voice_t);
   ST_PLUGIN_VOICE_SHARED_CAST(env_4_shared_t);

   float out = 0.0f;
   (void)out;
   // -------- lane "prepare" modIdx=0 modString=$m_ad
   
   // -- mod="$m_ad" dstVar=out
   out = voice->mod_ad_cur;
   
   // -- mod="clp" dstVar=out
   if(out > 1.0f) out = 1.0f;
   else if(out < 0.0f) out = 0.0f;
   
   // -- mod="pow" dstVar=out
   out = out * out * out;
   
   // -- mod="sto v_ad" dstVar=out
   voice->var_v_ad_next = out;
   
   // -- mod="$m_s" dstVar=out
   out = voice->mod_s_cur;
   
   // -- mod="sto v_s" dstVar=out
   voice->var_v_s_next = out;
   
   // -- mod="$m_r" dstVar=out
   out = voice->mod_r_cur;
   
   // -- mod="clp" dstVar=out
   if(out > 1.0f) out = 1.0f;
   else if(out < 0.0f) out = 0.0f;
   
   // -- mod="pow" dstVar=out
   out = out * out * out;
   
   // -- mod="sto v_r" dstVar=out
   voice->var_v_r_next = out;
   
   // -- mod="$m_scl" dstVar=out
   out = voice->mod_scl_cur;
   
   // -- mod="clp" dstVar=out
   if(out > 1.0f) out = 1.0f;
   else if(out < 0.0f) out = 0.0f;
   
   // -- mod="fma" dstVar=out
   out = (out * 0.0099f) + 0.01f;
   
   // -- mod="kbd" dstVar=out
   
   // ---- mod="kbd" input "amt" seq 1/1
   
   // -- mod="1" dstVar=voice->tmp1/*amt*/
   voice->tmp1/*amt*/ = 1.0f;
   voice->tmp2/*off*/ = 0;
   voice->tmp3/*rate*/  = (voice->note_cur + (voice->tmp2/*off*/ * 12.0f) - 60.0f) * voice->tmp1/*amt*/;
   out *= powf(2.0f, voice->tmp3/*rate*/ * (1.0f / 12.0f));
   
   // -- mod="sto v_scl" dstVar=out
   voice->var_v_scl_next = out;
} /* end prepare */


#ifndef CYCLE_SKIP_UI
static unsigned int loc_copy_chars(char *_d, const unsigned int _dSize, const char *_s) {
   unsigned int r = 0u;
   if(NULL != _d)
   {
      // Write min(dSize,len(s)) characters to 'd'
      char *d = _d;
      if(NULL != _s)
      {
         const char *s = _s;
         for(;;)
         {
            if(r < _dSize)
            {
               char c = s[r++];
               *d++ = c;
               if(0 == c)
                  break;
            }
            else
            {
               if(r > 0u)
                  d[-1] = 0;  // force ASCIIZ
               break;
            }
         }
      }
      else
      {
         if(_dSize > 0u)
         {
            _d[0] = 0;
            r = 1u;
         }
      }
   }
   else
   {
      // Return total length of source string
      if(NULL != _s)
      {
         const char *s = _s;
         for(;;)
         {
            char c = s[r++];
            if(0 == c)
               break;
         }
      }
   }
   return r;
}
#endif // CYCLE_SKIP_UI

