      voice->tmp7_env_last_gate = 0.0f;
      voice->tmp8_env_pos = 0.0f;
      voice->tmp9_env_rate = 0.0f;
      voice->var_x = 0.0f;
      voice->var_v_ad = 0.0f;
      voice->var_v_ad_next = 0.0f;
      voice->var_v_s = 0.0f;
      voice->var_v_s_next = 0.0f;
      voice->var_v_r = 0.0f;
      voice->var_v_r_next = 0.0f;
