   // -- mod="$m_lp_freq" dstVar=out
   out = voice->mod_lp_freq_cur;
   
   // -- mod="kbd" dstVar=out
   
   // ---- mod="kbd" input "amt" seq 1/1
   
   // -- mod="1" dstVar=voice->tmp1/*amt*/
   voice->tmp1/*amt*/ = 1.0f;
   voice->tmp2/*off*/ = 0;
   out *= 127.0f;
   voice->tmp3/*scl*/ = 4;
   out += (voice->note_cur + (voice->tmp2/*off*/ * 12.0f) - 64.0f) * voice->tmp1/*amt*/;
   out = clamp(out, 0.0f, 127.0f);
   #ifdef OVERSAMPLE_FACTOR
   out = ((440.0f/32.0f)*expf( ((out-9.0f)/12.0f)*logf(2.0f) )) / (voice->sample_rate * OVERSAMPLE_FACTOR);
   #else
   out = ((440.0f/32.0f)*expf( ((out-9.0f)/12.0f)*logf(2.0f) )) / voice->sample_rate;
   #endif
   out *= voice->tmp3/*scl*/;
   
   // -- mod="sto v_lp_freq" dstVar=out
   voice->var_v_lp_freq_next = out;
   
   // -- mod="$m_hp_freq" dstVar=out
   out = voice->mod_hp_freq_cur;
   
   // -- mod="kbd" dstVar=out
   
   // ---- mod="kbd" input "amt" seq 1/1
   
   // -- mod="1" dstVar=voice->tmp1/*amt*/
   voice->tmp1/*amt*/ = 1.0f;
   voice->tmp2/*off*/ = 0;
   out *= 127.0f;
   voice->tmp3/*scl*/ = 4;
   out += (voice->note_cur + (voice->tmp2/*off*/ * 12.0f) - 64.0f) * voice->tmp1/*amt*/;
   out = clamp(out, 0.0f, 127.0f);
   #ifdef OVERSAMPLE_FACTOR
   out = ((440.0f/32.0f)*expf( ((out-9.0f)/12.0f)*logf(2.0f) )) / (voice->sample_rate * OVERSAMPLE_FACTOR);
   #else
   out = ((440.0f/32.0f)*expf( ((out-9.0f)/12.0f)*logf(2.0f) )) / voice->sample_rate;
   #endif
