      voice->velocity = _vel;
      voice->tmp4_saw_phase = 0.0f;
      voice->tmp8_svf_lp = 0.0f;
      voice->tmp9_svf_hp = 0.0f;
      voice->tmp10_svf_bp = 0.0f;
      voice->tmp11_svf_lp = 0.0f;
      voice->tmp12_svf_hp = 0.0f;
      voice->tmp13_svf_bp = 0.0f;
      voice->var_x = 0.0f;
      voice->var_v_lp_freq = 0.0f;
      voice->var_v_lp_freq_next = 0.0f;
      voice->var_v_hp_freq = 0.0f;
      voice->var_v_hp_freq_next = 0.0f;
      voice->var_v_lp_last = 0.0f;
      voice->var_v_hp_last = 0.0f;
      voice->var_out__saw = 0.0f;
      voice->xlrf_0.reset();
